contracts/LockManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title FlashVault Lock Manager
 * @dev Manages time-locked USDT tokens with advanced features
 * @author FlashVault Team
 */
contract LockManager is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;

    // ============ STRUCTS ============
    
    struct Lock {
        uint256 id;
        address owner;
        address recipient;
        uint256 amount;
        uint256 unlockTime;
        bool isActive;
        bool isRedeemed;
        uint256 createdAt;
        uint256 lastModified;
    }

    struct SplitLock {
        uint256 originalLockId;
        uint256[] newLockIds;
        uint256 splitTime;
        bool isActive;
    }

    // ============ STATE VARIABLES ============
    
    IERC20 public immutable usdtToken;
    Counters.Counter private _lockIds;
    
    // Lock storage
    mapping(uint256 => Lock) public locks;
    mapping(address => uint256[]) public userLocks;
    mapping(uint256 => SplitLock) public splitLocks;
    
    // Configuration
    uint256 public minLockAmount = 100 * 10**6; // 100 USDT (6 decimals)
    uint256 public maxLockAmount = 1000000 * 10**6; // 1M USDT
    uint256 public minLockDuration = 1 days;
    uint256 public maxLockDuration = 365 days;
    uint256 public platformFee = 25; // 0.25% (basis points)
    uint256 public feeDenominator = 10000;
    
    // Events
    event LockCreated(
        uint256 indexed lockId,
        address indexed owner,
        address indexed recipient,
        uint256 amount,
        uint256 unlockTime
    );
    
    event LockRedeemed(
        uint256 indexed lockId,
        address indexed recipient,
        uint256 amount
    );
    
    event LockTransferred(
        uint256 indexed lockId,
        address indexed oldRecipient,
        address indexed newRecipient
    );
    
    event LockSplit(
        uint256 indexed originalLockId,
        uint256[] newLockIds,
        address indexed owner
    );
    
    event LockExtended(
        uint256 indexed lockId,
        uint256 newUnlockTime
    );
    
    event PlatformFeeUpdated(uint256 newFee);
    event LockLimitsUpdated(uint256 minAmount, uint256 maxAmount, uint256 minDuration, uint256 maxDuration);

    // ============ CONSTRUCTOR ============
    
    constructor(address _usdtToken) {
        require(_usdtToken != address(0), "Invalid USDT address");
        usdtToken = IERC20(_usdtToken);
        _transferOwnership(msg.sender);
    }

    // ============ MODIFIERS ============
    
    modifier onlyLockOwner(uint256 lockId) {
        require(locks[lockId].owner == msg.sender, "Not lock owner");
        _;
    }
    
    modifier onlyLockRecipient(uint256 lockId) {
        require(locks[lockId].recipient == msg.sender, "Not lock recipient");
        _;
    }
    
    modifier lockExists(uint256 lockId) {
        require(locks[lockId].id != 0, "Lock does not exist");
        _;
    }
    
    modifier lockActive(uint256 lockId) {
        require(locks[lockId].isActive, "Lock not active");
        _;
    }
    
    modifier lockNotRedeemed(uint256 lockId) {
        require(!locks[lockId].isRedeemed, "Lock already redeemed");
        _;
    }

    // ============ CORE FUNCTIONS ============
    
    /**
     * @dev Create a new time-lock
     * @param recipient Address that can redeem the lock
     * @param amount Amount of USDT to lock
     * @param unlockTime Timestamp when lock becomes redeemable
     */
    function createLock(
        address recipient,
        uint256 amount,
        uint256 unlockTime
    ) external nonReentrant returns (uint256) {
        require(recipient != address(0), "Invalid recipient");
        require(amount >= minLockAmount, "Amount too low");
        require(amount <= maxLockAmount, "Amount too high");
        require(unlockTime > block.timestamp + minLockDuration, "Duration too short");
        require(unlockTime <= block.timestamp + maxLockDuration, "Duration too long");
        
        // Calculate and collect platform fee
        uint256 fee = (amount * platformFee) / feeDenominator;
        uint256 lockAmount = amount - fee;
        
        // Transfer USDT from user to contract
        usdtToken.safeTransferFrom(msg.sender, address(this), amount);
        
        // Create lock
        _lockIds.increment();
        uint256 lockId = _lockIds.current();
        
        locks[lockId] = Lock({
            id: lockId,
            owner: msg.sender,
            recipient: recipient,
            amount: lockAmount,
            unlockTime: unlockTime,
            isActive: true,
            isRedeemed: false,
            createdAt: block.timestamp,
            lastModified: block.timestamp
        });
        
        userLocks[msg.sender].push(lockId);
        
        emit LockCreated(lockId, msg.sender, recipient, lockAmount, unlockTime);
        
        return lockId;
    }
    
    /**
     * @dev Redeem a lock (only by recipient after unlock time)
     * @param lockId ID of the lock to redeem
     */
    function redeemLock(uint256 lockId) external nonReentrant {
        Lock storage lock = locks[lockId];
        
        require(lock.isActive, "Lock not active");
        require(!lock.isRedeemed, "Lock already redeemed");
        require(lock.recipient == msg.sender, "Not lock recipient");
        require(block.timestamp >= lock.unlockTime, "Lock not yet unlocked");
        
        lock.isRedeemed = true;
        lock.lastModified = block.timestamp;
        
        // Transfer USDT to recipient
        usdtToken.safeTransfer(lock.recipient, lock.amount);
        
        emit LockRedeemed(lockId, lock.recipient, lock.amount);
    }
    
    /**
     * @dev Transfer lock ownership to new recipient
     * @param lockId ID of the lock to transfer
     * @param newRecipient New recipient address
     */
    function transferLock(uint256 lockId, address newRecipient) external {
        require(newRecipient != address(0), "Invalid recipient");
        
        Lock storage lock = locks[lockId];
        
        require(lock.isActive, "Lock not active");
        require(!lock.isRedeemed, "Lock already redeemed");
        require(lock.owner == msg.sender, "Not lock owner");
        
        address oldRecipient = lock.recipient;
        lock.recipient = newRecipient;
        lock.lastModified = block.timestamp;
        
        emit LockTransferred(lockId, oldRecipient, newRecipient);
    }
    
    /**
     * @dev Split a lock into multiple smaller locks
     * @param lockId ID of the lock to split
     * @param recipients Array of new recipient addresses
     * @param amounts Array of amounts for each split
     */
    function splitLock(
        uint256 lockId,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external nonReentrant {
        require(recipients.length == amounts.length, "Arrays length mismatch");
        require(recipients.length >= 2 && recipients.length <= 10, "Invalid split count");
        
        Lock storage originalLock = locks[lockId];
        
        require(originalLock.isActive, "Lock not active");
        require(!originalLock.isRedeemed, "Lock already redeemed");
        require(originalLock.owner == msg.sender, "Not lock owner");
        
        uint256 totalSplitAmount = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalSplitAmount += amounts[i];
        }
        
        require(totalSplitAmount == originalLock.amount, "Amounts must equal original");
        
        // Deactivate original lock
        originalLock.isActive = false;
        originalLock.lastModified = block.timestamp;
        
        // Create new locks
        uint256[] memory newLockIds = new uint256[](recipients.length);
        
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");
            require(amounts[i] > 0, "Invalid amount");
            
            _lockIds.increment();
            uint256 newLockId = _lockIds.current();
            
            locks[newLockId] = Lock({
                id: newLockId,
                owner: msg.sender,
                recipient: recipients[i],
                amount: amounts[i],
                unlockTime: originalLock.unlockTime,
                isActive: true,
                isRedeemed: false,
                createdAt: block.timestamp,
                lastModified: block.timestamp
            });
            
            userLocks[msg.sender].push(newLockId);
            newLockIds[i] = newLockId;
        }
        
        // Record split
        splitLocks[lockId] = SplitLock({
            originalLockId: lockId,
            newLockIds: newLockIds,
            splitTime: block.timestamp,
            isActive: true
        });
        
        emit LockSplit(lockId, newLockIds, msg.sender);
    }

    // ============ VIEW FUNCTIONS ============
    
    /**
     * @dev Get lock details
     * @param lockId ID of the lock
     * @return Lock details
     */
    function getLock(uint256 lockId) external view returns (Lock memory) {
        return locks[lockId];
    }
    
    /**
     * @dev Get all locks for a user
     * @param user User address
     * @return Array of lock IDs
     */
    function getUserLocks(address user) external view returns (uint256[] memory) {
        return userLocks[user];
    }
    
    /**
     * @dev Get total locked amount for a user
     * @param user User address
     * @return Total locked amount
     */
    function getUserTotalLocked(address user) external view returns (uint256) {
        uint256[] memory userLockIds = userLocks[user];
        uint256 total = 0;
        
        for (uint256 i = 0; i < userLockIds.length; i++) {
            Lock memory lock = locks[userLockIds[i]];
            if (lock.isActive && !lock.isRedeemed) {
                total += lock.amount;
            }
        }
        
        return total;
    }
    
    /**
     * @dev Check if lock is redeemable
     * @param lockId ID of the lock
     * @return True if redeemable
     */
    function isLockRedeemable(uint256 lockId) external view returns (bool) {
        Lock memory lock = locks[lockId];
        return lock.isActive && !lock.isRedeemed && block.timestamp >= lock.unlockTime;
    }
    
    /**
     * @dev Get contract statistics
     * @return Total locks, total locked amount, total fees collected
     */
    function getContractStats() external view returns (uint256, uint256, uint256) {
        uint256 totalLocks = _lockIds.current();
        uint256 totalLocked = 0;
        uint256 totalFees = 0;
        
        for (uint256 i = 1; i <= totalLocks; i++) {
            Lock memory lock = locks[i];
            if (lock.isActive && !lock.isRedeemed) {
                totalLocked += lock.amount;
            }
            // Calculate fees from original amounts
            uint256 originalAmount = (lock.amount * feeDenominator) / (feeDenominator - platformFee);
            totalFees += originalAmount - lock.amount;
        }
        
        return (totalLocks, totalLocked, totalFees);
    }

    // ============ ADMIN FUNCTIONS ============
    
    /**
     * @dev Update platform fee (only owner)
     * @param newFee New fee in basis points
     */
    function updatePlatformFee(uint256 newFee) external onlyOwner {
        require(newFee <= 100, "Fee too high"); // Max 1%
        platformFee = newFee;
        emit PlatformFeeUpdated(newFee);
    }
    
    /**
     * @dev Update lock limits (only owner)
     * @param _minAmount New minimum lock amount
     * @param _maxAmount New maximum lock amount
     * @param _minDuration New minimum lock duration
     * @param _maxDuration New maximum lock duration
     */
    function updateLockLimits(
        uint256 _minAmount,
        uint256 _maxAmount,
        uint256 _minDuration,
        uint256 _maxDuration
    ) external onlyOwner {
        require(_minAmount < _maxAmount, "Invalid amount range");
        require(_minDuration < _maxDuration, "Invalid duration range");
        require(_maxDuration <= 365 days, "Max duration too long");
        
        minLockAmount = _minAmount;
        maxLockAmount = _maxAmount;
        minLockDuration = _minDuration;
        maxLockDuration = _maxDuration;
        
        emit LockLimitsUpdated(_minAmount, _maxAmount, _minDuration, _maxDuration);
    }
    
    /**
     * @dev Withdraw collected fees (only owner)
     * @param amount Amount to withdraw
     */
    function withdrawFees(uint256 amount) external onlyOwner {
        require(amount <= address(this).balance, "Insufficient balance");
        payable(owner()).transfer(amount);
    }
    
    /**
     * @dev Emergency withdraw USDT (only owner, emergency use only)
     * @param amount Amount to withdraw
     */
    function emergencyWithdrawUSDT(uint256 amount) external onlyOwner {
        usdtToken.safeTransfer(owner(), amount);
    }
}

