// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title TetherUSDBridgedZED20
 * @dev Bridged USDT token for ZED ecosystem
 * @author FlashVault Team
 */
contract TetherUSDBridgedZED20 is ERC20, Ownable {
    uint8 private _decimals = 6; // USDT standard decimals
    
    constructor() ERC20("Tether USD Bridged", "USDT") {
        // Initial supply can be minted by owner for bridge operations
        _transferOwnership(msg.sender);
    }
    
    /**
     * @dev Mint new tokens (only owner/bridge)
     * @param to Recipient address
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }
    
    /**
     * @dev Burn tokens (only owner/bridge)
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }
    
    /**
     * @dev Override decimals to match USDT standard
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }
    
    /**
     * @dev Emergency function to recover any ERC20 tokens sent to this contract
     * @param tokenAddress Address of the token to recover
     * @param to Recipient address
     * @param amount Amount to recover
     */
    function recoverERC20(address tokenAddress, address to, uint256 amount) external onlyOwner {
        require(tokenAddress != address(this), "Cannot recover own token");
        IERC20(tokenAddress).transfer(to, amount);
    }
}

contracts/TokenBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title TokenBridge
 * @dev Bridge contract for cross-chain token transfers
 * @author FlashVault Team
 */
contract TokenBridge is ReentrancyGuard, Ownable, Pausable {
    using SafeERC20 for IERC20;
    
    IERC20 public immutable token;
    
    // Bridge state
    mapping(bytes32 => bool) public processedHashes;
    mapping(address => bool) public authorizedRelayers;
    
    // Events
    event TokensLocked(
        address indexed sender,
        uint256 amount,
        uint256 targetChainId,
        address indexed recipient,
        bytes32 indexed transferId
    );
    
    event TokensUnlocked(
        bytes32 indexed transferId,
        address indexed recipient,
        uint256 amount
    );
    
    event RelayerAuthorized(address indexed relayer);
    event RelayerRevoked(address indexed relayer);
    
    // Modifiers
    modifier onlyRelayer() {
        require(authorizedRelayers[msg.sender], "Only authorized relayer");
        _;
    }
    
    modifier transferNotProcessed(bytes32 transferId) {
        require(!processedHashes[transferId], "Transfer already processed");
        _;
    }
    
    constructor(address _token) {
        require(_token != address(0), "Invalid token address");
        token = IERC20(_token);
        _transferOwnership(msg.sender);
    }
    
    /**
     * @dev Lock tokens for cross-chain transfer
     * @param amount Amount of tokens to lock
     * @param targetChainId Target chain ID
     * @param recipient Recipient address on target chain
     */
    function lockTokens(
        uint256 amount,
        uint256 targetChainId,
        address recipient
    ) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(recipient != address(0), "Invalid recipient address");
        require(targetChainId != block.chainid, "Cannot bridge to same chain");
        
        // Transfer tokens from user to bridge
        token.safeTransferFrom(msg.sender, address(this), amount);
        
        // Generate unique transfer ID
        bytes32 transferId = keccak256(
            abi.encodePacked(
                msg.sender,
                amount,
                targetChainId,
                recipient,
                block.timestamp,
                block.chainid
            )
        );
        
        emit TokensLocked(msg.sender, amount, targetChainId, recipient, transferId);
    }
    
    /**
     * @dev Unlock tokens on target chain (only relayer)
     * @param transferId Unique transfer identifier
     * @param recipient Recipient address
     * @param amount Amount to unlock
     * @param sourceChainId Source chain ID
     * @param sourceTxHash Source transaction hash
     */
    function unlockTokens(
        bytes32 transferId,
        address recipient,
        uint256 amount,
        uint256 sourceChainId,
        bytes32 sourceTxHash
    ) external onlyRelayer transferNotProcessed(transferId) whenNotPaused {
        require(recipient != address(0), "Invalid recipient address");
        require(amount > 0, "Amount must be greater than 0");
        
        // Mark transfer as processed
        processedHashes[transferId] = true;
        
        // Mint tokens to recipient
        // Note: This requires the token contract to allow minting by bridge
        // You may need to modify the token contract or use a different approach
        
        emit TokensUnlocked(transferId, recipient, amount);
    }
    
    /**
     * @dev Authorize a relayer (only owner)
     * @param relayer Relayer address to authorize
     */
    function authorizeRelayer(address relayer) external onlyOwner {
        require(relayer != address(0), "Invalid relayer address");
        authorizedRelayers[relayer] = true;
        emit RelayerAuthorized(relayer);
    }
    
    /**
     * @dev Revoke relayer authorization (only owner)
     * @param relayer Relayer address to revoke
     */
    function revokeRelayer(address relayer) external onlyOwner {
        require(authorizedRelayers[relayer], "Relayer not authorized");
        authorizedRelayers[relayer] = false;
        emit RelayerRevoked(relayer);
    }
    
    /**
     * @dev Pause bridge operations (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause bridge operations (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Emergency withdraw tokens (only owner)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        token.safeTransfer(owner(), amount);
    }
    
    /**
     * @dev Get bridge balance
     */
    function getBridgeBalance() external view returns (uint256) {
        return token.balanceOf(address(this));
    }
    
    /**
     * @dev Check if transfer is processed
     * @param transferId Transfer ID to check
     */
    function isTransferProcessed(bytes32 transferId) external view returns (bool) {
        return processedHashes[transferId];
    }
}

